import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import './Navbar.css';

function Navbar() {
    const [menuOpen, setMenuOpen] = useState(false);

    const toggleMenu = () => {
        setMenuOpen(!menuOpen);
    };

    return (
        <nav>
            <div className="navbar-container">
                {/* Logo */}
                <div className="navbar-logo">
                    <Link to="/">Iglesia Admin</Link>
                </div>

                {/* Menu Icon for Mobile */}
                <div className={`menu-icon ${menuOpen ? 'hidden' : ''}`} onClick={toggleMenu}>
                    ☰
                </div>
                <div className={`close-icon ${menuOpen ? '' : 'hidden'}`} onClick={toggleMenu}>
                    ✖
                </div>

                {/* Navbar Links */}
                <ul className={`navbar-links ${menuOpen ? 'open' : ''}`}>
                    <li>
                        <Link to="/crear" onClick={toggleMenu}>Crear Registro</Link>
                    </li>
                    <li>
                        <Link to="/consultar" onClick={toggleMenu}>Consultar Registros</Link>
                    </li>
                    <li>
                        <Link to="/actualizar" onClick={toggleMenu}>Actualizar Registro</Link>
                    </li>
                    <li>
                        <Link to="/eliminar" onClick={toggleMenu}>Eliminar Registro</Link>
                    </li>
                </ul>
            </div>
        </nav>
    );
}

export default Navbar;
